freeOperator :: Parser String
freeOperator = grabToken nonFree <?> "free operator"
  where 
    nonFree (Operator op) | not (op `elem` predefinedOps) = Just op
    nonFree _ = Nothing
    
    wordOps = ["and then", "and", "or else", "or", "implies","xor"]

    predefinedOps = concat [ ["*","+","-"]
                           , ["<=",">=","=","/=","~","/~"]
                           , ["<<", ">>"]
                           , ["<",">"]
                           , ["\"[","]\""]
                           , [":=","?=","{","}"]
                           , wordOps
                           ]

